import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router";
import { adminSignIn } from "../../actions/admin";
import { mentorSignIn, mentorSignUp } from "../../actions/mentor";
import { studentSignIn, studentSignUp } from "../../actions/student";
import ArrowRight from "../../assets/icons/ArrowRight";
import loginBg from "../../assets/images/login.png";
import ReCAPTCHA from "react-google-recaptcha";
import { toast, ToastContainer } from "react-toastify";
import { showToast } from "../toast/toast";
import { Checkbox, FormControlLabel } from "@mui/material";
import { CSSTransition } from "react-transition-group";
import ModalOverlay from "../modal/ModalOverlay";
import ForgotPasswordModal from "./ForgotPasswordModal";
import { verifyRecaptcha } from "../../actions";

const Auth = ({ location }) => {
    const [toggleLogin, setToggleLogin] = useState(false);
    const [fields, setFields] = useState({
        firstName: "",
        lastName: "",
        middleName: "",
        email: "",
        password: "",
        confirmPassword: "",
        enrollmentNo: "",
        semester: "",
        department: "",
    });
    const dispatch = useDispatch();
    const history = useHistory();

    const validRoles = ["Admin", "Mentor", "Mentee"];

    useEffect(() => {
        if (!location.state || !validRoles.includes(location.state.trim())) {
            showToast("error", "Invalid role. Redirecting...", 3000, toast.POSITION.TOP_RIGHT);
            setTimeout(() => {
                history.goBack();
            }, 2000);
        }
    }, [location.state, history]);

    const resetFields = () => {
        setFields({
            firstName: "",
            lastName: "",
            middleName: "",
            email: "",
            password: "",
            confirmPassword: "",
            enrollmentNo: "",
            semester: "",
            department: "",
        });
    };

    const handleToggle = () => {
        setToggleLogin(!toggleLogin);
        resetFields();
    };

    const handleChange = (e) => {
        if (e.target.name === "semester" && e.target.value === "") return;
        setFields({ ...fields, [e.target.name]: e.target.value.trim() });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!location.state || !validRoles.includes(location.state.trim())) {
            showToast("error", "Invalid role selected", 3000, toast.POSITION.TOP_RIGHT);
            return;
        }
        if (!recaptcha && !toggleLogin) {
            showToast("error", "Please complete the reCAPTCHA", 3000, toast.POSITION.TOP_RIGHT);
            return;
        }

        if (location.state === "Admin") {
            dispatch(adminSignIn(fields, history));
        } else if (location.state === "Mentor") {
            if (toggleLogin) {
                if (fields.password !== fields.confirmPassword) {
                    showToast("error", "Passwords don't match", 3000, toast.POSITION.TOP_RIGHT);
                    return;
                }
                dispatch(mentorSignUp(fields, handleToggle));
            } else {
                dispatch(mentorSignIn(fields, history));
            }
        } else if (location.state === "Mentee") {
            if (toggleLogin) {
                if (fields.password !== fields.confirmPassword) {
                    showToast("error", "Passwords don't match", 3000, toast.POSITION.TOP_RIGHT);
                    return;
                }
                dispatch(studentSignUp(fields, handleToggle));
            } else {
                dispatch(studentSignIn(fields, history));
            }
        }

        resetFields();
    };

    const [recaptcha, setRecaptcha] = useState(false);

    const handleCaptchaChange = async (val) => {
        if (val) {
            try {
                const isValid = await dispatch(verifyRecaptcha(val));
                setRecaptcha(isValid);
            } catch (error) {
                setRecaptcha(false);
                showToast("error", "Failed to verify reCAPTCHA. Please try again.", 3000, toast.POSITION.TOP_RIGHT);
            }
        } else {
            setRecaptcha(false);
        }
    };

    const [showPass, setShowPass] = useState("password");
    const [FPEmail, setFPEmail] = useState({
        role: location.state,
        email: "",
    });

    const handlePasswordShowToggle = () => {
        setShowPass((prev) => (prev === "password" ? "text" : "password"));
    };

    const [showModal, setShowModal] = useState(false);
    const modalRef = useRef(null);
    const overlayRef = useRef(null);

    return (
        <div className="w-full h-screen flex items-center">
            <CSSTransition
                nodeRef={overlayRef}
                in={showModal}
                timeout={300}
                classNames="overlay"
                unmountOnExit
            >
                <ModalOverlay nodeRef={overlayRef} />
            </CSSTransition>
            <CSSTransition
                nodeRef={modalRef}
                in={showModal}
                timeout={300}
                classNames="modal"
                unmountOnExit
            >
                <ForgotPasswordModal
                    nodeRef={modalRef}
                    setShowModal={setShowModal}
                    setFPEmail={setFPEmail}
                    FPEmail={FPEmail}
                />
            </CSSTransition>
            <div className="flex-3 bg-white h-full flex flex-col items-center justify-center">
                <div className="w-full">
                    <h1 style={{ fontSize: "36px" }} className="w-full text-center">
                        <span className="text-teal-500">{location.state}</span>{" "}
                        {toggleLogin ? "Sign up" : "Sign in"}
                    </h1>
                </div>
                <img src={loginBg} alt="" className="w-1/2" />
            </div>
            <div className="flex-2 bg-teal-600 h-full flex items-center justify-center">
                <div className="w-96">
                    <form onSubmit={handleSubmit}>
                        {toggleLogin && (
                            <div className="grid grid-cols-3 gap-2">
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="firstName" className="mb-2 text-white">
                                        First name
                                    </label>
                                    <input
                                        id="firstName"
                                        name="firstName"
                                        type="text"
                                        value={fields.firstName}
                                        onChange={handleChange}
                                        required
                                        className="rounded-md border-none"
                                    />
                                </div>
                            </div>
                        )}
                        <div className="flex flex-col mb-6">
                            <label htmlFor="email" className="mb-2 text-white">Email Address</label>
                            <input
                                id="email"
                                name="email"
                                type="email"
                                value={fields.email}
                                onChange={handleChange}
                                required
                                className="rounded-lg border-none"
                            />
                        </div>
                        <div className="flex flex-col mb-6">
                            <label htmlFor="password" className="mb-2 text-white">Password</label>
                            <input
                                id="password"
                                name="password"
                                type={showPass}
                                value={fields.password}
                                onChange={handleChange}
                                required
                                className="rounded-lg border-none"
                            />
                            <span onClick={handlePasswordShowToggle} className="text-teal-300 cursor-pointer">
                                {showPass === "password" ? "Show" : "Hide"} password
                            </span>
                        </div>
                        {toggleLogin && (
                            <div className="flex flex-col mb-6">
                                <label htmlFor="confirmPassword" className="mb-2 text-white">
                                    Confirm Password
                                </label>
                                <input
                                    id="confirmPassword"
                                    name="confirmPassword"
                                    type={showPass}
                                    value={fields.confirmPassword}
                                    onChange={handleChange}
                                    required
                                    className="rounded-lg border-none"
                                />
                            </div>
                        )}
                        <div className="flex items-center mb-6">
                            <ReCAPTCHA
                                sitekey="YOUR_SITE_KEY"
                                onChange={handleCaptchaChange}
                            />
                        </div>
                        <div className="flex items-center justify-between">
                            <button type="button" onClick={() => setShowModal(true)} className="text-teal-300">
                                Forgot Password?
                            </button>
                            <button
                                type="submit"
                                className="bg-gradient-to-r from-teal-400 to-teal-600 text-white font-bold rounded-lg py-3 px-6 shadow-md hover:shadow-lg transition duration-300 ease-in-out"
                            >
                                {toggleLogin ? "Sign Up" : "Sign In"}
                                <ArrowRight className="inline-block ml-2" />
                            </button>
                        </div>
                    </form>
                    <ToastContainer limit={5} draggable={false} pauseOnFocusLoss={false} />
                </div>
            </div>
        </div>
    );
};

export default Auth;
