import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { Roles } from "../utility";
import bg from "../assets/images/bg-2.png";
import { FormControl, FormControlLabel, Radio, RadioGroup } from "@mui/material";
import ArrowRight from "../assets/icons/ArrowRight";

const Main = () => {
    const user = JSON.parse(localStorage.getItem("authData"));
    const history = useHistory();
    const [selectedRole, setSelectedRole] = useState("Admin");

    // Redirect the user to the required dashboard if a user is present
    useEffect(() => {
        if (user?.role) {
            switch (user.role) {
                case Roles.ADMIN:
                    history.push("/admin/dashboard");
                    break;
                case Roles.MENTOR:
                    history.push("/mentor/dashboard");
                    break;
                case Roles.STUDENT:
                    history.push("/mentee/dashboard");
                    break;
                default:
                    break;
            }
        }
    }, [user, history]);

    const handleRoleChange = (event) => {
        setSelectedRole(event.target.value);
    };

    return (
        <div className="flex h-screen w-full items-center">
            {/* Left Side: Welcome Section */}
            <div className="flex-1 bg-white h-full flex flex-col items-center justify-center p-10 shadow-lg">
                <div className="w-full">
                    <h1 className="text-4xl font-bold text-blue-500 text-center">
                        Welcome, to
                    </h1>
                    <h2 className="text-2xl font-semibold text-center">
                        Student Mentoring System
                    </h2>
                </div>
                <img src={bg} alt="Background" className="w-3/4 mt-6" />
            </div>

            {/* Right Side: Role Selection Section */}
            <div className="flex-1 bg-blue-500 h-full flex flex-col items-center justify-center text-white p-10 gap-8">
                <h2 className="text-2xl font-semibold">Select Your Role</h2>
                <FormControl component="fieldset">
                    <RadioGroup
                        row
                        aria-label="role-options"
                        name="role-options"
                        value={selectedRole}
                        onChange={handleRoleChange}
                        className="space-x-4"
                    >
                        {["Admin", "Mentor", "Mentee"].map((role) => (
                            <FormControlLabel
                                key={role}
                                value={role}
                                control={
                                    <Radio
                                        sx={{
                                            color: "white",
                                            "&.Mui-checked": { color: "white" },
                                        }}
                                    />
                                }
                                label={role}
                            />
                        ))}
                    </RadioGroup>
                </FormControl>
                <Link
                    to={`/${selectedRole.toLowerCase()}`}
                    className="bg-white text-blue-500 px-6 py-2 rounded-full flex items-center justify-center gap-x-2 hover:bg-blue-700 hover:text-white transition-transform duration-300 transform hover:-translate-y-1"
                >
                    Next
                    <ArrowRight
                        alt={false}
                        myStyle={"h-6 w-6 group-hover:translate-x-1 transition-transform"}
                    />
                </Link>
            </div>
        </div>
    );
};

export default Main;
