import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { Roles } from "../utility";
import backgroundImage from "../assets/images/bg-2.png";
import { FormControl, FormControlLabel, Radio, RadioGroup } from "@mui/material";
import ArrowIcon from "../assets/icons/ArrowRight";

const Home = () => {
    const storedUser = JSON.parse(localStorage.getItem("authData"));
    const navigation = useHistory();
    const [selectedRole, setSelectedRole] = useState("Admin");

    useEffect(() => {
        // Only redirect if user is authenticated and tries to access the Home component
        if (storedUser && storedUser.role) {
            switch (storedUser.role) {
                case Roles.ADMIN:
                    navigation.replace("/admin/dashboard");
                    break;
                case Roles.MENTOR:
                    navigation.replace("/mentor/dashboard");
                    break;
                case Roles.STUDENT:
                    navigation.replace("/mentee/dashboard");
                    break;
                default:
                    break;
            }
        }
    }, [storedUser, navigation]);

    const handleRoleChange = (event) => {
        setSelectedRole(event.target.value);
    };

    <Link
    to={{
        pathname: `/${selectedRole.toLowerCase()}`,
        state: selectedRole, // Pass the role as location state
    }}
>
    {/* Link content */}
</Link>

    return (
        <div className="flex h-screen w-full items-center justify-center bg-gray-200">
            <div className="h-full w-1/2 flex flex-col items-center justify-center bg-white p-10 shadow-lg">
                <h1 className="text-4xl font-bold text-teal-500 mb-6 text-center">
                    Welcome to STEPuHP Mentoring System
                </h1>
                <img src={backgroundImage} alt="Background" className="w-3/4 mt-6" />
            </div>
            <div className="h-full w-1/2 flex flex-col items-center justify-center bg-teal-600 text-white p-10 gap-8">
                <h2 className="text-2xl font-semibold">Select Your User Role</h2>
                <FormControl component="fieldset">
                    <RadioGroup
                        row
                        aria-label="role-options"
                        name="role-options"
                        value={selectedRole}
                        onChange={handleRoleChange}
                        className="space-x-4"
                    >
                        {["Admin", "Mentor", "Mentee"].map((role) => (
                            <FormControlLabel
                                key={role}
                                value={role}
                                control={
                                    <Radio
                                        sx={{
                                            color: "white",
                                            "&.Mui-checked": { color: "white" },
                                        }}
                                    />
                                }
                                label={role}
                            />
                        ))}
                    </RadioGroup>
                </FormControl>
                <Link
                    to={`/${selectedRole.toLowerCase()}`}
                    className="bg-white text-teal-600 px-6 py-2 rounded-full flex items-center justify-center gap-x-2 hover:bg-teal-800 hover:text-white transition-transform duration-300 transform hover:-translate-y-1"
                >
                    Next
                    <ArrowIcon
                        alt={false}
                        myStyle={"h-6 w-6 group-hover:translate-x-1 transition-transform"}
                    />
                </Link>
            </div>
        </div>
    );
};

export default Home;
