import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router";

import { adminSignIn } from "../../actions/admin";
import { mentorSignIn, mentorSignUp } from "../../actions/mentor";
import { studentSignIn, studentSignUp } from "../../actions/student";
import ArrowRight from "../../assets/icons/ArrowRight";
import loginBg from "../../assets/images/login.png";
import ReCAPTCHA from "react-google-recaptcha";
import { toast, ToastContainer } from "react-toastify";
import { showToast } from "../toast/toast";
import { Checkbox, FormControlLabel } from "@mui/material";
import { CSSTransition } from "react-transition-group";
import ModalOverlay from "../modal/ModalOverlay";
import ForgotPasswordModal from "./ForgotPasswordModal";
import { verifyRecaptcha } from "../../actions";

const Auth = ({ location }) => {
    const [toggleLogin, setToggleLogin] = useState(false);
    const [fields, setFields] = useState({
        firstName: "",
        lastName: "",
        middleName: "",
        email: "",
        password: "",
        confirmPassword: "",
        enrollmentNo: "",
        semester: "",
        department: "",
    });
    const dispatch = useDispatch();
    const history = useHistory();

    useEffect(() => {
        if (!location.state || !["Admin", "Mentor", "Mentee"].includes(location.state)) {
            history.goBack();
        }
    }, [location.state, history]);

    const resetFields = () => {
        setFields({
            firstName: "",
            lastName: "",
            middleName: "",
            email: "",
            password: "",
            confirmPassword: "",
            enrollmentNo: "",
            semester: "",
            department: "",
        });
    };

    const handleToggle = () => {
        setToggleLogin(!toggleLogin);
        resetFields();
    };

    const handleChange = (e) => {
        if (e.target.name === "semester" && e.target.value === "") return;
        setFields({ ...fields, [e.target.name]: e.target.value.trim() });
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (!location.state || !["Admin", "Mentor", "Mentee"].includes(location.state)) {
            showToast("error", "Invalid role selection", 5000, toast.POSITION.TOP_RIGHT);
            return;
        }

        if (toggleLogin === false && !recaptcha) {
            showToast("error", "Please complete the reCAPTCHA", 5000, toast.POSITION.TOP_RIGHT);
            return;
        }

        if (location.state === "Admin") {
            dispatch(adminSignIn(fields, history));
        } else if (location.state === "Mentor") {
            if (toggleLogin) {
                if (fields.password !== fields.confirmPassword) {
                    showToast("error", "Passwords don't match", 5000, toast.POSITION.TOP_RIGHT);
                    return;
                }
                dispatch(mentorSignUp(fields, handleToggle));
            } else {
                dispatch(mentorSignIn(fields, history));
            }
        } else if (location.state === "Mentee") {
            if (toggleLogin) {
                if (fields.password !== fields.confirmPassword) {
                    showToast("error", "Passwords don't match", 5000, toast.POSITION.TOP_RIGHT);
                    return;
                }
                dispatch(studentSignUp(fields, handleToggle));
            } else {
                dispatch(studentSignIn(fields, history));
            }
        }

        resetFields();
    };

    const [recaptcha, setRecaptcha] = useState(true);

    const handleCaptchaChange = (val) => {
        dispatch(verifyRecaptcha(val, setRecaptcha));
    };

    const [showPass, setShowPass] = useState("password");
    const [FPEmail, setFPEmail] = useState({
        role: location.state,
        email: "",
    });

    const handlePasswordShowToggle = () => {
        setShowPass(showPass === "password" ? "text" : "password");
    };

    const [showModal, setShowModal] = useState(false);
    const modalRef = useRef(null);
    const overlayRef = useRef(null);

    return (
        <div className="w-full h-screen flex items-center">
            <CSSTransition
                nodeRef={overlayRef}
                in={showModal}
                timeout={300}
                classNames="overlay"
                unmountOnExit
            >
                <ModalOverlay nodeRef={overlayRef} />
            </CSSTransition>
            <CSSTransition
                nodeRef={modalRef}
                in={showModal}
                timeout={300}
                classNames="modal"
                unmountOnExit
            >
                <ForgotPasswordModal
                    nodeRef={modalRef}
                    setShowModal={setShowModal}
                    setFPEmail={setFPEmail}
                    FPEmail={FPEmail}
                />
            </CSSTransition>
            <div className="flex-3 bg-white h-full flex flex-col items-center justify-center">
                <div className="w-full">
                    <h1 style={{ fontSize: "50px" }} className="w-full text-center">
                        <span className="text-blue-500">{location.state}</span>{" "}
                        {toggleLogin ? "sign-up" : "sign-in"}
                    </h1>
                </div>
                <img src={loginBg} alt="" className="w-1/2" />
            </div>
            <div className="flex-2 bg-gray-600 h-full flex items-center justify-center">
                <div className="w-96">
                    <form className="" onSubmit={handleSubmit}>
                        {/* Form Fields */}
                        {toggleLogin || (
                            <ReCAPTCHA
                                className="flex items-center justify-center"
                                sitekey={process.env.REACT_APP_RECAPTCHA_KEY}
                                onChange={handleCaptchaChange}
                            />
                        )}

                        <button
                            type="submit"
                            className="bg-white py-2 px-3 rounded-full flex items-center justify-center gap-x-2 w-full text-gray-600 group mt-4 disabled:opacity-50"
                            disabled={toggleLogin === false && recaptcha ? true : false}
                        >
                            {toggleLogin ? "Sign up" : "Sign in"}
                            <ArrowRight
                                alt={false}
                                myStyle={"h-4 w-4 group-hover:translate-x-2 transform transition"}
                            />
                        </button>
                    </form>
                    {location.state === "Admin" ? (
                        ""
                    ) : (
                        <div className="flex flex-col justify-center items-center">
                            <h4 className="mt-5 text-white">
                                {toggleLogin
                                    ? "Already have an account?"
                                    : "Don't have an account?"}
                            </h4>

                            <button
                                onClick={handleToggle}
                                className="rounded-md px-2 py-1 text-white hover:underline"
                            >
                                {toggleLogin ? "Sign in" : "Sign up"}
                            </button>
                        </div>
                    )}
                </div>
            </div>
            <ToastContainer limit={5} draggable={false} pauseOnFocusLoss={false} />
        </div>
    );
};

export default Auth;
