import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { Roles } from "../utility";
import bg from "../assets/images/bg-2.png";
import { FormControl, FormControlLabel, Radio, RadioGroup, Typography, Button } from "@mui/material";
import ArrowRight from "../assets/icons/ArrowRight";

const Main = () => {
    const user = JSON.parse(localStorage.getItem("authData"));
    const history = useHistory();
    const [value, setValue] = useState("Admin");

    useEffect(() => {
        if (user) {
            switch (user.role) {
                case Roles.ADMIN:
                    history.push("/admin/dashboard");
                    break;
                case Roles.MENTOR:
                    history.push("/mentor/dashboard");
                    break;
                case Roles.STUDENT:
                    history.push("/mentee/dashboard");
                    break;
                default:
                    break;
            }
        }
    }, [user, history]);

    const handleChange = (e) => {
        setValue(e.target.value);
    };

    return (
        <div className="flex h-screen">
            <div className="flex-2 bg-white flex flex-col items-center justify-center p-6">
                <div className="text-center">
                    <Typography variant="h1" className="text-blue-500" gutterBottom>
                        Welcome
                    </Typography>
                    <Typography variant="h2" gutterBottom>
                        Student Mentoring System
                    </Typography>
                </div>
                <img src={bg} alt="Background" className="w-1/2" />
            </div>
            <div className="flex-grow bg-blue-500 flex flex-col items-center justify-center gap-y-8 text-white p-6">
                <Typography variant="h5">Select your role</Typography>
                <FormControl component="fieldset">
                    <RadioGroup
                        value={value}
                        onChange={handleChange}
                        row
                        aria-label="user-role-selection"
                    >
                        {["Admin", "Mentor", "Mentee"].map((role) => (
                            <FormControlLabel
                                key={role}
                                value={role}
                                control={<Radio sx={{ color: "white", '&.Mui-checked': { color: "white" } }} />}
                                label={role}
                            />
                        ))}
                    </RadioGroup>
                </FormControl>
                <Link
                    to={`/${value.toLowerCase()}`}
                    className="bg-white py-2 px-4 rounded-full flex items-center justify-center gap-x-2 text-blue-700 transition-transform duration-300 transform hover:scale-105"
                >
                    <Typography variant="button">Next</Typography>
                    <ArrowRight alt={false} className="h-4 w-4" />
                </Link>
            </div>
        </div>
    );
};

export default Main;
