import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from "react-redux";
import { useHistory, useLocation } from "react-router";
import { adminSignIn } from "../../actions/admin";
import { mentorSignIn, mentorSignUp } from "../../actions/mentor";
import { studentSignIn, studentSignUp } from "../../actions/student";
import ArrowRight from "../../assets/icons/ArrowRight";
import loginBg from "../../assets/images/login.png";
import ReCAPTCHA from "react-google-recaptcha";
import { toast, ToastContainer } from "react-toastify";
import { showToast } from "../toast/toast";
import { Checkbox, FormControlLabel } from "@mui/material";
import { CSSTransition } from "react-transition-group";
import ModalOverlay from "../modal/ModalOverlay";
import ForgotPasswordModal from "./ForgotPasswordModal";
import { verifyRecaptcha } from "../../actions";

const Auth = () => {
    const location = useLocation();
    const history = useHistory();
    const dispatch = useDispatch();

    // State for role and toggle form type
    const [role, setRole] = useState(location.state || "Admin");
    const [toggleLogin, setToggleLogin] = useState(false);

    // Form fields state
    const [fields, setFields] = useState({
        firstName: "",
        lastName: "",
        middleName: "",
        email: "",
        password: "",
        confirmPassword: "",
        enrollmentNo: "",
        semester: "",
        department: "",
    });

    // Update role on location change
    useEffect(() => {
        if (!location.state) {
            setRole("Admin");
            history.replace({ state: "Admin" });
        } else {
            setRole(location.state);
        }
    }, [location.state, history]);

    // Reset form fields
    const resetFields = () => {
        setFields({
            firstName: "",
            lastName: "",
            middleName: "",
            email: "",
            password: "",
            confirmPassword: "",
            enrollmentNo: "",
            semester: "",
            department: "",
        });
    };

    // Toggle between login and sign-up forms
    const handleToggle = () => {
        setToggleLogin(!toggleLogin);
        resetFields();
    };

    // Handle form field changes
    const handleChange = (e) => {
        setFields((prevFields) => ({
            ...prevFields,
            [e.target.name]: e.target.value.trim() ? e.target.value.trim() : prevFields[e.target.name],
        }));
    };

    // Handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();

        if (toggleLogin && fields.password !== fields.confirmPassword) {
            showToast("error", "Passwords don't match", 5000, toast.POSITION.TOP_RIGHT);
            return;
        }

        // Dispatch actions based on role and login/signup mode
        if (role === "Admin") {
            dispatch(adminSignIn(fields, history));
        } else if (role === "Mentor") {
            toggleLogin ? dispatch(mentorSignUp(fields, handleToggle)) : dispatch(mentorSignIn(fields, history));
        } else if (role === "Mentee") {
            toggleLogin ? dispatch(studentSignUp(fields, handleToggle)) : dispatch(studentSignIn(fields, history));
        }

        resetFields();
    };

    // ReCAPTCHA handling
    const [recaptcha, setRecaptcha] = useState(true);
    const handleCaptchaChange = (val) => {
        dispatch(verifyRecaptcha(val, setRecaptcha));
    };

    // Password visibility toggle
    const [showPass, setShowPass] = useState("password");
    const handlePasswordShowToggle = () => {
        setShowPass(showPass === "password" ? "text" : "password");
    };

    // Forgot password modal state
    const [showModal, setShowModal] = useState(false);
    const modalRef = useRef(null);
    const overlayRef = useRef(null);

    return (
        <div className="w-full h-screen flex items-center">
            {/* Modal components */}
            <CSSTransition nodeRef={overlayRef} in={showModal} timeout={300} classNames="overlay" unmountOnExit>
                <ModalOverlay nodeRef={overlayRef} />
            </CSSTransition>
            <CSSTransition nodeRef={modalRef} in={showModal} timeout={300} classNames="modal" unmountOnExit>
                <ForgotPasswordModal nodeRef={modalRef} setShowModal={setShowModal} />
            </CSSTransition>

            {/* Left section with title and image */}
            <div className="flex-3 bg-white h-full flex flex-col items-center justify-center">
                <h1 style={{ fontSize: "50px" }} className="text-center mb-4">
                    <span className="text-blue-500">{role}</span> {toggleLogin ? "Sign-Up" : "Sign-In"}
                </h1>
                <img src={loginBg} alt="Login background" className="w-1/2" />
            </div>

            {/* Right section with the form */}
            <div className="flex-2 bg-gray-600 h-full flex items-center justify-center">
                <div className="w-96">
                    <form onSubmit={handleSubmit}>
                        {/* Fields visible only in sign-up mode */}
                        {toggleLogin && (
                            <div className="grid grid-cols-3 gap-2">
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="firstName" className="mb-2 text-white">First name</label>
                                    <input id="firstName" name="firstName" type="text" value={fields.firstName} onChange={handleChange} required className="rounded-md border-none" />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="middleName" className="mb-2 text-white">Middle name</label>
                                    <input id="middleName" name="middleName" type="text" value={fields.middleName} onChange={handleChange} className="rounded-lg border-none" />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="lastName" className="mb-2 text-white">Last name</label>
                                    <input id="lastName" name="lastName" type="text" value={fields.lastName} onChange={handleChange} required className="rounded-lg border-none" />
                                </div>
                            </div>
                        )}

                        {/* Fields visible only for Mentee role in sign-up mode */}
                        {toggleLogin && role === "Mentee" && (
                            <div className="grid grid-cols-2 gap-2 mb-6">
                                <div className="flex flex-col">
                                    <label htmlFor="enrollmentNo" className="mb-2 text-white">Enrollment No.</label>
                                    <input id="enrollmentNo" name="enrollmentNo" type="text" value={fields.enrollmentNo.toUpperCase()} onChange={handleChange} required className="rounded-lg border-none" />
                                </div>
                                <div className="flex flex-col">
                                    <label htmlFor="semester" className="mb-2 text-white">Semester</label>
                                    <select id="semester" name="semester" className="rounded-lg border-none" value={fields.semester} onChange={handleChange} required>
                                        <option value="">Select semester</option>
                                        <option value="1st semester">1st semester</option>
                                        {/* Add other semesters here */}
                                    </select>
                                </div>
                            </div>
                        )}

                        {/* Common fields for login and sign-up */}
                        <div className="flex flex-col mb-6">
                            <label htmlFor="email" className="mb-2 text-white">Email</label>
                            <input id="email" name="email" type="email" value={fields.email} onChange={handleChange} required className="rounded-md border-none" />
                        </div>
                        <div className="flex flex-col mb-6">
                            <label htmlFor="password" className="mb-2 text-white">Password</label>
                            <input id="password" name="password" type={showPass} value={fields.password} onChange={handleChange} required className="rounded-md border-none" />
                            <FormControlLabel control={<Checkbox onChange={handlePasswordShowToggle} />} label="Show password" className="text-white" />
                        </div>
                        {toggleLogin && (
                            <div className="flex flex-col mb-6">
                                <label htmlFor="confirmPassword" className="mb-2 text-white">Confirm Password</label>
                                <input id="confirmPassword" name="confirmPassword" type={showPass} value={fields.confirmPassword} onChange={handleChange} required className="rounded-md border-none" />
                            </div>
                        )}

                        {/* ReCAPTCHA for login */}
                        {!toggleLogin && (
                            <div className="flex justify-center mb-6">
                                <ReCAPTCHA sitekey="YOUR_RECAPTCHA_SITE_KEY" onChange={handleCaptchaChange} />
                            </div>
                        )}

                        {/* Submit and toggle button */}
                        <button type="submit" disabled={!recaptcha} className="w-full py-3 mt-4 bg-blue-500 text-white rounded-md flex justify-center items-center">
                            <span className="mr-2">{toggleLogin ? "Sign Up" : "Sign In"}</span>
                            <ArrowRight />
                        </button>
                        <button type="button" onClick={handleToggle} className="w-full py-3 mt-4 bg-gray-500 text-white rounded-md">
                            {toggleLogin ? "Switch to Sign In" : "Switch to Sign Up"}
                        </button>
                    </form>
                </div>
            </div>
            <ToastContainer limit={5} draggable={false} pauseOnFocusLoss={false} />
        </div>
    );
};

export default Auth;
