import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router";

import { adminSignIn } from "../../actions/admin";
import { mentorSignIn, mentorSignUp } from "../../actions/mentor";
import { studentSignIn, studentSignUp } from "../../actions/student";
import ArrowRight from "../../assets/icons/ArrowRight";
import loginBg from "../../assets/images/login.png";
import ReCAPTCHA from "react-google-recaptcha";
import { toast, ToastContainer } from "react-toastify";
import { showToast } from "../toast/toast";
import { Checkbox, FormControlLabel } from "@mui/material";
import { CSSTransition } from "react-transition-group";
import ModalOverlay from "../modal/ModalOverlay";
import ForgotPasswordModal from "./ForgotPasswordModal";
import { verifyRecaptcha } from "../../actions";

const Auth = ({ location }) => {
    const [toggleLogin, setToggleLogin] = useState(false);
    const [fields, setFields] = useState({
        firstName: "",
        lastName: "",
        middleName: "",
        email: "",
        password: "",
        confirmPassword: "",
        enrollmentNo: "",
        semester: "",
        department: "",
    });
    const dispatch = useDispatch();
    const history = useHistory();

    useEffect(() => {
        if (location.state === undefined) {
            history.goBack();
        }
    }, [location.state, history]);

    const resetFields = () => {
        setFields({
            firstName: "",
            lastName: "",
            middleName: "",
            email: "",
            password: "",
            confirmPassword: "",
            enrollmentNo: "",
            semester: "",
            department: "",
        });
    };

    const handleToggle = () => {
        setToggleLogin(!toggleLogin);
        resetFields();
    };

    const handleChange = (e) => {
        setFields({ ...fields, [e.target.name]: e.target.value.trim() });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (toggleLogin && recaptcha) return;

        if (location.state === "Admin") {
            dispatch(adminSignIn(fields, history));
        } else if (location.state === "Mentor") {
            if (toggleLogin) {
                if (fields.password !== fields.confirmPassword) {
                    showToast("error", "Passwords don't match", 5000, toast.POSITION.TOP_RIGHT);
                    return;
                }
                dispatch(mentorSignUp(fields, handleToggle));
            } else {
                dispatch(mentorSignIn(fields, history));
            }
        } else if (location.state === "Mentee") {
            if (toggleLogin) {
                if (fields.password !== fields.confirmPassword) {
                    showToast("error", "Passwords don't match", 5000, toast.POSITION.TOP_RIGHT);
                    return;
                }
                dispatch(studentSignUp(fields, handleToggle));
            } else {
                dispatch(studentSignIn(fields, history));
            }
        }
        resetFields();
    };

    const [recaptcha, setRecaptcha] = useState(true);
    const handleCaptchaChange = (val) => {
        dispatch(verifyRecaptcha(val, setRecaptcha));
    };

    const [showPass, setShowPass] = useState("password");
    const [FPEmail, setFPEmail] = useState({
        role: location.state,
        email: "",
    });
    const handlePasswordShowToggle = () => {
        setShowPass(showPass === "password" ? "text" : "password");
    };

    const [showModal, setShowModal] = useState(false);
    const modalRef = useRef(null);
    const overlayRef = useRef(null);

    return (
        <div className="w-full h-screen flex items-center bg-gray-100">
            <CSSTransition
                nodeRef={overlayRef}
                in={showModal}
                timeout={300}
                classNames="overlay"
                unmountOnExit
            >
                <ModalOverlay nodeRef={overlayRef} />
            </CSSTransition>
            <CSSTransition
                nodeRef={modalRef}
                in={showModal}
                timeout={300}
                classNames="modal"
                unmountOnExit
            >
                <ForgotPasswordModal
                    nodeRef={modalRef}
                    setShowModal={setShowModal}
                    setFPEmail={setFPEmail}
                    FPEmail={FPEmail}
                />
            </CSSTransition>

            <div className="flex-1 bg-white shadow-lg rounded-lg mx-8 p-6">
                <div className="text-center mb-6">
                    <h1 className="text-2xl font-bold text-blue-600">
                        {toggleLogin ? "Sign Up" : "Sign In"} as {location.state}
                    </h1>
                    <img src={loginBg} alt="Login Background" className="w-1/2 mx-auto mt-4" />
                </div>
                <form onSubmit={handleSubmit} className="space-y-4">
                    {toggleLogin && (
                        <div className="grid grid-cols-3 gap-2">
                            <div>
                                <label htmlFor="firstName" className="block text-gray-700">First Name</label>
                                <input
                                    id="firstName"
                                    name="firstName"
                                    type="text"
                                    value={fields.firstName}
                                    onChange={handleChange}
                                    required
                                    className="rounded-lg border border-gray-300 p-2 w-full"
                                />
                            </div>
                            <div>
                                <label htmlFor="middleName" className="block text-gray-700">Middle Name</label>
                                <input
                                    id="middleName"
                                    name="middleName"
                                    type="text"
                                    value={fields.middleName}
                                    onChange={handleChange}
                                    className="rounded-lg border border-gray-300 p-2 w-full"
                                />
                            </div>
                            <div>
                                <label htmlFor="lastName" className="block text-gray-700">Last Name</label>
                                <input
                                    id="lastName"
                                    name="lastName"
                                    type="text"
                                    value={fields.lastName}
                                    onChange={handleChange}
                                    required
                                    className="rounded-lg border border-gray-300 p-2 w-full"
                                />
                            </div>
                        </div>
                    )}
                    {toggleLogin && location.state === "Mentee" ? (
                        <div className="grid grid-cols-2 gap-2">
                            <div>
                                <label htmlFor="enrollmentNo" className="block text-gray-700">Enrollment No.</label>
                                <input
                                    id="enrollmentNo"
                                    name="enrollmentNo"
                                    type="text"
                                    value={fields.enrollmentNo.toUpperCase()}
                                    onChange={handleChange}
                                    required
                                    className="rounded-lg border border-gray-300 p-2 w-full"
                                />
                            </div>
                            <div>
                                <label htmlFor="semester" className="block text-gray-700">Semester</label>
                                <select
                                    id="semester"
                                    name="semester"
                                    className="rounded-lg border border-gray-300 p-2 w-full"
                                    value={fields.semester}
                                    onChange={handleChange}
                                    required
                                >
                                    <option value="">Select semester</option>
                                    <option value="1st semester">1st semester</option>
                                    <option value="2nd semester">2nd semester</option>
                                    <option value="3rd semester">3rd semester</option>
                                    <option value="4th semester">4th semester</option>
                                    <option value="5th semester">5th semester</option>
                                    <option value="6th semester">6th semester</option>
                                    <option value="7th semester">7th semester</option>
                                    <option value="8th semester">8th semester</option>
                                    <option value="9th semester">9th semester</option>
                                    <option value="10th semester">10th semester</option>
                                </select>
                            </div>
                            <div>
                                <label htmlFor="department" className="block text-gray-700">Department</label>
                                <input
                                    id="department"
                                    name="department"
                                    type="text"
                                    value={fields.department}
                                    onChange={handleChange}
                                    required
                                    className="rounded-lg border border-gray-300 p-2 w-full"
                                />
                            </div>
                            <div>
                                <label htmlFor="email" className="block text-gray-700">Email</label>
                                <input
                                    id="email"
                                    name="email"
                                    type="email"
                                    value={fields.email}
                                    onChange={handleChange}
                                    required
                                    className="rounded-lg border border-gray-300 p-2 w-full"
                                />
                            </div>
                        </div>
                    ) : (
                        <div>
                            <label htmlFor="email" className="block text-gray-700">Email</label>
                            <input
                                id="email"
                                name="email"
                                type="email"
                                value={fields.email}
                                onChange={handleChange}
                                required
                                className="rounded-lg border border-gray-300 p-2 w-full"
                            />
                        </div>
                    )}
                    <div>
                        <label htmlFor="password" className="block text-gray-700">Password</label>
                        <input
                            id="password"
                            type={showPass}
                            name="password"
                            value={fields.password}
                            onChange={handleChange}
                            required
                            className="rounded-lg border border-gray-300 p-2 w-full"
                        />
                        <button type="button" onClick={handlePasswordShowToggle} className="text-blue-500 mt-1">
                            {showPass === "password" ? "Show" : "Hide"} Password
                        </button>
                    </div>
                    {toggleLogin && (
                        <div>
                            <label htmlFor="confirmPassword" className="block text-gray-700">Confirm Password</label>
                            <input
                                id="confirmPassword"
                                type={showPass}
                                name="confirmPassword"
                                value={fields.confirmPassword}
                                onChange={handleChange}
                                required
                                className="rounded-lg border border-gray-300 p-2 w-full"
                            />
                        </div>
                    )}
                    <FormControlLabel
                        control={<Checkbox name="recaptcha" checked={recaptcha} onChange={() => setRecaptcha(!recaptcha)} />}
                        label="I agree to the terms and conditions"
                        style={{ color: "gray" }}
                    />
                    {toggleLogin && recaptcha && (
                        <ReCAPTCHA
                            sitekey="YOUR_RECAPTCHA_SITE_KEY"
                            onChange={handleCaptchaChange}
                        />
                    )}
                    <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded-lg">
                        {toggleLogin ? "Sign Up" : "Sign In"}
                        <ArrowRight className="inline-block ml-2" />
                    </button>
                </form>
                <div className="mt-4 text-center text-gray-600">
                    <span>{toggleLogin ? "Already have an account?" : "Don't have an account?"}</span>
                    <button onClick={handleToggle} className="text-blue-500 ml-2">
                        {toggleLogin ? "Sign In" : "Sign Up"}
                    </button>
                </div>
                <div className="mt-4 text-center">
                    <button onClick={() => setShowModal(true)} className="text-blue-500">
                        Forgot Password?
                    </button>
                </div>
            </div>
            <ToastContainer />
        </div>
    );
};

export default Auth;
