import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from "react-redux";
import { useHistory, useLocation } from "react-router-dom";

import { adminSignIn } from "../../actions/admin";
import { mentorSignIn, mentorSignUp } from "../../actions/mentor";
import { studentSignIn, studentSignUp } from "../../actions/student";
import ArrowRight from "../../assets/icons/ArrowRight";
import loginBg from "../../assets/images/login.png";
import ReCAPTCHA from "react-google-recaptcha";
import { toast, ToastContainer } from "react-toastify";
import { showToast } from "../toast/toast";
import { Checkbox, FormControlLabel } from "@mui/material";
import { CSSTransition } from "react-transition-group";
import ModalOverlay from "../modal/ModalOverlay";
import ForgotPasswordModal from "./ForgotPasswordModal";
import { verifyRecaptcha } from "../../actions";

const Auth = () => {
    const location = useLocation();
    const [userRole, setUserRole] = useState(location.state || "");

    // state variables declaration
    const [toggleLogin, setToggleLogin] = useState(false);
    const [fields, setFields] = useState({
        firstName: "",
        lastName: "",
        middleName: "",
        email: "",
        password: "",
        confirmPassword: "",
        enrollmentNo: "",
        semester: "",
        department: "",
    });
    const dispatch = useDispatch();
    const history = useHistory();

    // Using useEffect to handle navigation and state persistence
    useEffect(() => {
        if (!userRole) {
            // Check if the userRole is empty, then redirect to home
            history.push('/'); // Redirect to home if no role is selected
        }
    }, [userRole, history]);

    // functions to control the form
    const resetFields = () => {
        // This function is used to reset the form fields to ""
        setFields({
            firstName: "",
            lastName: "",
            middleName: "",
            email: "",
            password: "",
            confirmPassword: "",
            enrollmentNo: "",
            semester: "",
            department: "",
        });
    };

    const handleToggle = () => {
        // This function is used to toggle between signin and signup
        setToggleLogin(!toggleLogin);
        resetFields();
    };

    const handleChange = (e) => {
        // This function is used to set the new form field values
        if (e.target.name === "semester" && e.target.value === "") return;
        setFields({ ...fields, [e.target.name]: e.target.value.trim() });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (toggleLogin === false && recaptcha) return;

        try {
            if (userRole === "Admin") {
                await dispatch(adminSignIn(fields, history));
            } else if (userRole === "Mentor") {
                if (toggleLogin) {
                    if (fields.password !== fields.confirmPassword) {
                        showToast("error", "passwords don't match", 5000, toast.POSITION.TOP_RIGHT);
                        return;
                    }
                    await dispatch(mentorSignUp(fields, handleToggle));
                } else {
                    await dispatch(mentorSignIn(fields, history));
                }
            } else if (userRole === "Mentee") {
                if (toggleLogin) {
                    if (fields.password !== fields.confirmPassword) {
                        showToast("error", "passwords don't match", 5000, toast.POSITION.TOP_RIGHT);
                        return;
                    }
                    await dispatch(studentSignUp(fields, handleToggle));
                } else {
                    await dispatch(studentSignIn(fields, history));
                }
            }
            resetFields();
        } catch (error) {
            showToast("error", "Authentication failed", 5000, toast.POSITION.TOP_RIGHT);
        }
    };

    // state for the login button dependent on recaptcha
    const [recaptcha, setRecaptcha] = useState(true);

    // function to handle captcha and send to backend
    const handleCaptchaChange = (val) => {
        dispatch(verifyRecaptcha(val, setRecaptcha));
    };

    // state to show and hide password
    const [showPass, setShowPass] = useState("password");
    // state for forgot password email
    const [FPEmail, setFPEmail] = useState({
        role: userRole,
        email: "",
    });

    // function to toggle show password state
    const handlePasswordShowToggle = () => {
        setShowPass((prevState) => (prevState === "password" ? "text" : "password"));
    };

    // states for modal
    const [showModal, setShowModal] = useState(false);
    const modalRef = useRef(null);
    const overlayRef = useRef(null);

    // Update FPEmail role when userRole changes
    useEffect(() => {
        setFPEmail((prev) => ({ ...prev, role: userRole }));
    }, [userRole]);

    if (!userRole) {
        return null; // Or a loading state
    }

    return (
        <div className="w-full h-screen flex items-center">
            <CSSTransition
                nodeRef={overlayRef}
                in={showModal}
                timeout={300}
                classNames="overlay"
                unmountOnExit
            >
                <ModalOverlay nodeRef={overlayRef} />
            </CSSTransition>
            <CSSTransition
                nodeRef={modalRef}
                in={showModal}
                timeout={300}
                classNames="modal"
                unmountOnExit
            >
                <ForgotPasswordModal
                    nodeRef={modalRef}
                    setShowModal={setShowModal}
                    setFPEmail={setFPEmail}
                    FPEmail={FPEmail}
                />
            </CSSTransition>
            <div className="flex-3 bg-white h-full flex flex-col items-center justify-center">
                <div className="w-full">
                    <h1 style={{ fontSize: "50px" }} className="w-full text-center">
                        <span className="text-blue-500">{userRole}</span>{" "}
                        {toggleLogin ? "sign-up" : "sign-in"}
                    </h1>
                </div>
                <img src={loginBg} alt="" className="w-1/2" />
            </div>
            <div className="flex-2 bg-gray-600 h-full flex items-center justify-center">
                <div className="w-96">
                    <form className="" onSubmit={handleSubmit}>
                        {toggleLogin && (
                            <div className="grid grid-cols-3 gap-2">
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="firstName" className="mb-2 text-white">
                                        First name
                                    </label>
                                    <input
                                        id="firstName"
                                        name="firstName"
                                        type="text"
                                        value={fields.firstName}
                                        onChange={handleChange}
                                        required
                                        className="rounded-md border-none"
                                    />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="middleName" className="mb-2 text-white">
                                        Middle name
                                    </label>
                                    <input
                                        id="middleName"
                                        name="middleName"
                                        type="text"
                                        value={fields.middleName}
                                        onChange={handleChange}
                                        className="rounded-lg border-none"
                                    />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="lastName" className="mb-2 text-white">
                                        Last name
                                    </label>
                                    <input
                                        id="lastName"
                                        name="lastName"
                                        type="text"
                                        value={fields.lastName}
                                        onChange={handleChange}
                                        required
                                        className="rounded-lg border-none"
                                    />
                                </div>
                            </div>
                        )}
                        {toggleLogin && userRole === "Mentee" ? (
                            <div className="grid grid-cols-2 gap-2">
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="enrollmentNo" className="mb-2 text-white">
                                        Enrollment No.
                                    </label>
                                    <input
                                        id="enrollmentNo"
                                        name="enrollmentNo"
                                        type="text"
                                        value={fields.enrollmentNo.toUpperCase()}
                                        onChange={handleChange}
                                        required
                                        className="rounded-lg border-none"
                                    />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="semester" className="mb-2 text-white">
                                        Semester
                                    </label>
                                    <input
                                        id="semester"
                                        name="semester"
                                        type="number"
                                        value={fields.semester}
                                        onChange={handleChange}
                                        required
                                        className="rounded-lg border-none"
                                    />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="department" className="mb-2 text-white">
                                        Department
                                    </label>
                                    <input
                                        id="department"
                                        name="department"
                                        type="text"
                                        value={fields.department}
                                        onChange={handleChange}
                                        required
                                        className="rounded-lg border-none"
                                    />
                                </div>
                            </div>
                        ) : null}
                        <div className="flex flex-col mb-6">
                            <label htmlFor="email" className="mb-2 text-white">
                                Email
                            </label>
                            <input
                                id="email"
                                name="email"
                                type="email"
                                value={fields.email}
                                onChange={handleChange}
                                required
                                className="rounded-lg border-none"
                            />
                        </div>
                        <div className="flex flex-col mb-6">
                            <label htmlFor="password" className="mb-2 text-white">
                                Password
                            </label>
                            <input
                                id="password"
                                name="password"
                                type={showPass}
                                value={fields.password}
                                onChange={handleChange}
                                required
                                className="rounded-lg border-none"
                            />
                        </div>
                        {toggleLogin && (
                            <div className="flex flex-col mb-6">
                                <label htmlFor="confirmPassword" className="mb-2 text-white">
                                    Confirm Password
                                </label>
                                <input
                                    id="confirmPassword"
                                    name="confirmPassword"
                                    type={showPass}
                                    value={fields.confirmPassword}
                                    onChange={handleChange}
                                    required
                                    className="rounded-lg border-none"
                                />
                            </div>
                        )}
                        <FormControlLabel
                            control={
                                <Checkbox
                                    color="default"
                                    checked={showPass === "text"}
                                    onChange={handlePasswordShowToggle}
                                />
                            }
                            label="Show Password"
                            className="text-white"
                        />
                        <button
                            type="submit"
                            className={`bg-white py-2 px-3 rounded-full flex items-center justify-center gap-x-2 w-full text-gray-600 group mt-4`}
                        >
                            {toggleLogin ? "Sign up" : "Sign in"}
                            <ArrowRight alt={false} myStyle={"h-4 w-4 group-hover:translate-x-2 transform transition"} />
                        </button>
                        <div className="flex justify-between text-white mt-4">
                            <button
                                type="button"
                                onClick={() => setShowModal(true)}
                                className="underline"
                            >
                                Forgot password?
                            </button>
                            <button type="button" onClick={handleToggle} className="underline">
                                {toggleLogin ? "Already have an account?" : "Create an account"}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
            <ToastContainer limit={5} draggable={false} pauseOnFocusLoss={false} />
            <ReCAPTCHA
                sitekey={process.env.REACT_APP_RECAPTCHA_SITE_KEY}
                onChange={handleCaptchaChange}
                className="hidden"
            />
        </div>
    );
};

export default Auth;
