import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";
import { adminSignIn } from "../../actions/admin";
import { mentorSignIn, mentorSignUp } from "../../actions/mentor";
import { studentSignIn, studentSignUp } from "../../actions/student";
import ArrowRight from "../../assets/icons/ArrowRight";
import loginBg from "../../assets/images/login.png";
import ReCAPTCHA from "react-google-recaptcha";
import { toast, ToastContainer } from "react-toastify";
import ModalOverlay from "../modal/ModalOverlay";
import ForgotPasswordModal from "./ForgotPasswordModal";
import { CSSTransition } from "react-transition-group";

const Auth = ({ location }) => {
    const [toggleLogin, setToggleLogin] = useState(false);
    const [fields, setFields] = useState({
        firstName: "",
        lastName: "",
        middleName: "",
        email: "",
        password: "",
        confirmPassword: "",
        enrollmentNo: "",
        semester: "",
        department: "",
    });
    const [recaptchaToken, setRecaptchaToken] = useState(null);
    const dispatch = useDispatch();
    const history = useHistory();

    const validRoles = ["Admin", "Mentor", "Mentee"];

    useEffect(() => {
        // Validate incoming role
        if (!location.state || !validRoles.includes(location.state.trim())) {
            toast.error("Invalid role. Redirecting...");
            setTimeout(() => {
                history.goBack();
            }, 2000);
        }
    }, [location.state, history]);

    const resetFields = () => {
        setFields({
            firstName: "",
            lastName: "",
            middleName: "",
            email: "",
            password: "",
            confirmPassword: "",
            enrollmentNo: "",
            semester: "",
            department: "",
        });
    };

    const handleToggle = () => {
        setToggleLogin(!toggleLogin);
        resetFields();
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFields((prevFields) => ({
            ...prevFields,
            [name]: value.trim(),
        }));
    };

    const handleCaptchaChange = (token) => {
        setRecaptchaToken(token);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (!recaptchaToken) {
            toast.error("Please complete the reCAPTCHA");
            return;
        }

        switch (location.state) {
            case "Admin":
                dispatch(adminSignIn(fields, history, recaptchaToken));
                break;
            case "Mentor":
                if (toggleLogin) {
                    if (fields.password !== fields.confirmPassword) {
                        toast.error("Passwords do not match");
                        return;
                    }
                    dispatch(mentorSignUp(fields, handleToggle, recaptchaToken));
                } else {
                    dispatch(mentorSignIn(fields, history, recaptchaToken));
                }
                break;
            case "Mentee":
                if (toggleLogin) {
                    if (fields.password !== fields.confirmPassword) {
                        toast.error("Passwords do not match");
                        return;
                    }
                    dispatch(studentSignUp(fields, handleToggle, recaptchaToken));
                } else {
                    dispatch(studentSignIn(fields, history, recaptchaToken));
                }
                break;
            default:
                break;
        }

        resetFields();
        setRecaptchaToken(null);
    };

    const [showPass, setShowPass] = useState("password");
    const [showModal, setShowModal] = useState(false);
    const modalRef = useRef(null);
    const overlayRef = useRef(null);

    return (
        <div className="w-full h-screen flex items-center">
            <CSSTransition nodeRef={overlayRef} in={showModal} timeout={300} classNames="overlay" unmountOnExit>
                <ModalOverlay nodeRef={overlayRef} />
            </CSSTransition>
            <CSSTransition nodeRef={modalRef} in={showModal} timeout={300} classNames="modal" unmountOnExit>
                <ForgotPasswordModal nodeRef={modalRef} setShowModal={setShowModal} />
            </CSSTransition>
            <div className="flex-3 bg-white h-full flex flex-col items-center justify-center">
                <h1 className="text-5xl">
                    <span className="text-blue-500">{location.state}</span> {toggleLogin ? "Sign Up" : "Sign In"}
                </h1>
                <img src={loginBg} alt="Login Background" className="w-1/2" />
            </div>
            <div className="flex-2 bg-gray-600 h-full flex items-center justify-center">
                <div className="w-96">
                    <form onSubmit={handleSubmit}>
                        {toggleLogin && (
                            <div className="grid grid-cols-3 gap-2">
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="firstName" className="text-white">First Name</label>
                                    <input id="firstName" name="firstName" type="text" value={fields.firstName} onChange={handleChange} required className="rounded-md border-none" />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="middleName" className="text-white">Middle Name</label>
                                    <input id="middleName" name="middleName" type="text" value={fields.middleName} onChange={handleChange} className="rounded-lg border-none" />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="lastName" className="text-white">Last Name</label>
                                    <input id="lastName" name="lastName" type="text" value={fields.lastName} onChange={handleChange} required className="rounded-lg border-none" />
                                </div>
                            </div>
                        )}
                        {toggleLogin && location.state === "Mentee" && (
                            <div className="grid grid-cols-3 gap-2">
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="enrollmentNo" className="text-white">Enrollment No</label>
                                    <input id="enrollmentNo" name="enrollmentNo" type="text" value={fields.enrollmentNo} onChange={handleChange} required className="rounded-lg border-none" />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="semester" className="text-white">Semester</label>
                                    <input id="semester" name="semester" type="number" min="1" max="8" value={fields.semester} onChange={handleChange} required className="rounded-lg border-none" />
                                </div>
                                <div className="flex flex-col mb-6">
                                    <label htmlFor="department" className="text-white">Department</label>
                                    <input id="department" name="department" type="text" value={fields.department} onChange={handleChange} required className="rounded-lg border-none" />
                                </div>
                            </div>
                        )}
                        <div className="flex flex-col mb-6">
                            <label htmlFor="email" className="text-white">Email</label>
                            <input id="email" name="email" type="email" value={fields.email} onChange={handleChange} required className="rounded-lg border-none" />
                        </div>
                        <div className="flex flex-col mb-6">
                            <label htmlFor="password" className="text-white">Password</label>
                            <input id="password" name="password" type={showPass} value={fields.password} onChange={handleChange} required className="rounded-lg border-none" />
                            <span onClick={() => setShowPass((prev) => (prev === "password" ? "text" : "password"))} className="text-blue-300 cursor-pointer">
                                {showPass === "password" ? "Show" : "Hide"} password
                            </span>
                        </div>
                        {toggleLogin && (
                            <div className="flex flex-col mb-6">
                                <label htmlFor="confirmPassword" className="text-white">Confirm Password</label>
                                <input id="confirmPassword" name="confirmPassword" type={showPass} value={fields.confirmPassword} onChange={handleChange} required className="rounded-lg border-none" />
                            </div>
                        )}
                        <div className="flex items-center mb-6">
                            <ReCAPTCHA
                                sitekey={process.env.REACT_APP_RECAPTCHA_SITE_KEY}
                                onChange={handleCaptchaChange}
                            />
                        </div>
                        <div className="flex items-center justify-between">
                            <button type="button" onClick={() => setShowModal(true)} className="text-blue-300">Forgot Password?</button>
                            <button type="submit" className="bg-blue-500 text-white rounded-lg py-2 px-4">
                                {toggleLogin ? "Sign Up" : "Sign In"}
                                <ArrowRight className="inline-block ml-2" />
                            </button>
                        </div>
                    </form>
                    <ToastContainer limit={5} draggable={false} pauseOnFocusLoss={false} />
                </div>
            </div>
        </div>
    );
};

export default Auth;
